import{_ as a,o as e,c as o,f as r}from"./app.74e3ecb9.js";var l="/create-keyboard/1.png",n="/create-keyboard/2.png",i="/create-keyboard/3.png",t="/create-keyboard/4.png",s="/create-keyboard/5.png",p="/create-keyboard/6.png",k="/create-keyboard/7.png",m="/create-keyboard/8.png",u="/create-keyboard/9.png",v="/create-keyboard/10.png",c="/create-keyboard/11.png";const P='{"title":"Kiel krei Esperantan klavararan\u011Don \u0109e Vindozo","description":"\u0108u vi ne plu volas uzi programojn de ekstera liveranto por skribi \u0109apelliterojn a\u016D vi simple volas lerni kiel fari siajn proprajn klavararan\u011Dojn? Se jes, tiu \u0109i instrulilo estas por vi!","frontmatter":{"title":"Kiel krei Esperantan klavararan\u011Don \u0109e Vindozo","description":"\u0108u vi ne plu volas uzi programojn de ekstera liveranto por skribi \u0109apelliterojn a\u016D vi simple volas lerni kiel fari siajn proprajn klavararan\u011Dojn? Se jes, tiu \u0109i instrulilo estas por vi!"},"headers":[{"level":2,"title":"Kiel krei la klavararan\u011Don","slug":"kiel-krei-la-klavararangon"},{"level":2,"title":"Kiel forigi la klavararan\u011Don","slug":"kiel-forigi-la-klavararangon"},{"level":2,"title":"Problemoj","slug":"problemoj"}],"relativePath":"pages/Esperanto/Teknologio/create-esperanto-layout-windows.md","lastUpdated":1709057980554}',d={},j=r('<h1 id="kiel-krei-esperantan-klavararangon-ce-vindozo">Kiel krei Esperantan klavararan\u011Don \u0109e Vindozo</h1><p>En \u0109i tiu instruilo mi montros al vi kiel krei Esperantan klavararan\u011Don, per kiu oni povas skribi \u0109apelliterojn premante klavon AltGr.</p><h2 id="kiel-krei-la-klavararangon">Kiel krei la klavararan\u011Don</h2><p>Unue, vi devas el\u015Duti programon, kiu ebligas krei proprajn klavararan\u011Dojn. Vi el\u015Dutos la programon el la oficiala retejo de Microsoft. Do ne zorgu, \u011Di ne enhavas komputilan viruson.</p><p><a href="https://www.microsoft.com/en-us/download/confirmation.aspx?id=102134" target="_blank" rel="noopener noreferrer">El\u015Dutoligilo</a></p><p>Poste malfermu la instalitan dosieron kaj se vi volas, \u015Dan\u011Du dosierujon, en kiun vi volas eltiri instalilon. Poste alklaku butonon <em>Extract</em>.</p><p class="center"><img src="'+l+'" alt="alt text"></p><p>Navigu al la dosierujo kaj malfermu dosieron nomatan <em>setup</em>.</p><p>Alklaku <em>Next</em>. Elektu <em>I Agree</em> por akcepti la interkonsento pri uzado. Poste da\u016Dre alklakadu la butonon <em>next</em>. Je la fino, alklaku la butonon <em>Close</em>.</p><p class="center"><img src="'+n+'" alt="alt text"></p><p>Entajpu <em>msklc</em> en la ser\u0109breton de Vindozo kaj malfermu la programon <em>Microsoft Keyboard Layout Creator 1.4</em>.</p><p>Kiam la programo lan\u0109os, alklaku la opcion <em>File</em> kaj elektu <em>Load Existing Keyboard...</em></p><p class="center"><img src="'+i+'" alt="alt text"></p><p>Tie vi elektu pretan klavararan\u011Don, mi elektis la usonanan internacian sed vi povas alian.</p><p>La klavaro i\u011Du plena. Nun vi redaktos minusklojn, elektu staton <em>Alt+Ctrl (AltGr)</em> kaj alklaku la klavojn por \u015Dan\u011Di ilian signon. Kiam vi \u015Dan\u011Das la signojn, memoru alklaki butonon <em>All...</em> kaj elekti la suban elektobutonon <em>caps = shift</em>, por ke CapsLock estu same traktata kiel Shift.</p><p class="center"><img src="'+t+'" alt="alt text"><img src="'+s+'" alt="alt text"></p><p>Faru same por aliaj klavoj, la rezulto tiel aspektu:</p><p class="center"><img src="'+p+'" alt="alt text"></p><p>Por redakti majusklojn, elektu staton Shift kaj denove alklaku la klavojn por \u015Dan\u011Di ilian signon. \u0108i-foje ne alklau la butonon <em>All...</em> La rezulto tiel aspektu:</p><p class="center"><img src="'+k+'" alt="alt text"></p><p>Nun alklaku opcion <em>Project</em> kaj elektu <em>Properties...</em> por \u015Dan\u011Di la nomon, la priskribon, la kopirajton kaj aliajn ecojn de la klavararan\u011Do.</p><p class="center"><img src="'+m+'" alt="alt text"></p><p>Se vi volas kontroli, \u0109u la klavararan\u011Do funkcias \u011Duste, alklaku opcion <em>Project</em> kaj elektu <em>Test Keyboard Layout...</em></p><p>Se \u0109io bonas, denove alklaku opcion <em>Project</em> kaj elektu <em>Build DLL and Setup Package...</em></p><p>Aperos fenestroj, sur la unua alklaku butonon <em>Ne</em> kaj <em>Jes</em> sur la dua, aperos dosierujo kun programo <em>setup</em>, malfermu \u011Din.</p><p class="center"><img src="'+u+'" alt="alt text"></p><p>Se vi tion vidas, vi povas fermi \u0109iujn fenestrojn kaj restartigi vian komputilon.</p><p>Gratulon! Nun vi povas \u015Dan\u011Di klavararan\u011Dojn uzante fulmoklavon Win + Spacostango.</p><h2 id="kiel-forigi-la-klavararangon">Kiel forigi la klavararan\u011Don</h2><p>Se vi volas forigi la klavararan\u011Don, renavigu al la dosierujo kun la programo <em>setup</em> kaj malfermu \u011Din. Alklaku butonon <em>Finish</em> kaj akceptu atingokontrolon. Kiu aperos sur taskopleto, kiel sur la bildo:</p><p class="center"><img src="'+v+'" alt="alt text"></p><p>Se vi tion vidas, vi sukcese forigis la klavararan\u011Don. Vi povas fermi \u0109iujn fenestrojn.</p><p class="center"><img src="'+c+'" alt="alt text"></p><h2 id="problemoj">Problemoj</h2><p>Se vi havis iujn ajn problemojn, sendu mesa\u011Don al mi per diskordo: janomi\u0125alako#9984, a\u016D per retpo\u015Dto: <a href="mailto:janmichalak@int.pl">janmichalak@int.pl</a></p>',35),g=[j];function _(f,b,h,x,y,S){return e(),o("div",null,g)}var K=a(d,[["render",_]]);export{P as __pageData,K as default};
