import{_ as o,c as e,o as s,a as i,g as a}from"./app.5ebd3efc.js";var t="/2-dzial/krolestwo-polskie.png",n="/2-dzial/Emilia_Plater.png",l="/2-dzial/Julian_Ordon.jpg";const zi='{"title":"Dzia\u0142 II - Ziemie polskie w pierwszej po\u0142owie XIX stulecia","description":"Notatka dotycz\u0105ca II rozdzia\u0142u podr\u0119cznika \\"Pozna\u0107 przesz\u0142o\u015B\u0107 3\\" wydawnictwa Nowej Ery.","frontmatter":{"title":"Dzia\u0142 II - Ziemie polskie w pierwszej po\u0142owie XIX stulecia","description":"Notatka dotycz\u0105ca II rozdzia\u0142u podr\u0119cznika \\"Pozna\u0107 przesz\u0142o\u015B\u0107 3\\" wydawnictwa Nowej Ery."},"headers":[{"level":2,"title":"1. System wiede\u0144ski","slug":"_1-system-wiedenski"},{"level":3,"title":"Nowy podzia\u0142 ziem polskich","slug":"nowy-podzial-ziem-polskich"},{"level":3,"title":"Sytuacja polityczna, spo\u0142eczna, gospodarcza ziem polskich","slug":"sytuacja-polityczna-spoleczna-gospodarcza-ziem-polskich"},{"level":2,"title":"2. Kr\xF3lestwo polskie","slug":"_2-krolestwo-polskie"},{"level":3,"title":"Sytuacja polityczna, gospodarcza, spo\u0142eczna Kr\xF3lestwa Polskiego","slug":"sytuacja-polityczna-gospodarcza-spoleczna-krolestwa-polskiego"},{"level":3,"title":"Dzia\u0142alno\u015B\u0107 Opozycji","slug":"dzialalnosc-opozycji"},{"level":3,"title":"Spiski","slug":"spiski"},{"level":2,"title":"3. Powstanie Listopadowe","slug":"_3-powstanie-listopadowe"},{"level":3,"title":"Noc listopadowa","slug":"noc-listopadowa"},{"level":3,"title":"Najwa\u017Cniejsze bitwy:","slug":"najwazniejsze-bitwy"},{"level":3,"title":"Postacie:","slug":"postacie"},{"level":2,"title":"4. Wielka emigracja","slug":"_4-wielka-emigracja"},{"level":3,"title":"Ugrupowania na emigracji","slug":"ugrupowania-na-emigracji"},{"level":3,"title":"Organizacje spiskowe w kraju","slug":"organizacje-spiskowe-w-kraju"},{"level":2,"title":"5. Wiosna lud\xF3w","slug":"_5-wiosna-ludow"},{"level":3,"title":"Rabacja galicyjska","slug":"rabacja-galicyjska"},{"level":3,"title":"Powstanie krakowskie","slug":"powstanie-krakowskie"},{"level":3,"title":"Wiosna lud\xF3w","slug":"wiosna-ludow"}],"relativePath":"pages/Polski/Szkola/Historia/2-dzial.md","lastUpdated":1637251924906}',w={},c=i("h1",{id:"dzial-ii-ziemie-polskie-w-pierwszej-polowie-xix-stulecia"},"Dzia\u0142 II - Ziemie polskie w pierwszej po\u0142owie XIX stulecia",-1),z=i("h2",{id:"_1-system-wiedenski"},"1. System wiede\u0144ski",-1),r=i("h3",{id:"nowy-podzial-ziem-polskich"},"Nowy podzia\u0142 ziem polskich",-1),d=i("p",null,"Po kongresie wiede\u0144skim (1815) dzieli si\u0119 ziemie polskie na:",-1),p=i("ul",null,[i("li",null,"Kr\xF3lestwo Polskie (w unii z Rosj\u0105);"),i("li",null,"Wielkie Ksi\u0119stwo Pozna\u0144skie (autonomiczna cz\u0119\u015B\u0107 Prus);"),i("li",null,"Rzeczpospolita Krakowska/Wolne Miasto Krak\xF3w (republika pod opiek\u0105 Rosji, Austrii i Prus).")],-1),k=i("p",{class:"center"},[i("img",{src:t,alt:"alt text"}),a(" Ugu\u0142em tak to wygl\u0105da\u0142o (stan na 1815).")],-1),y=i("h3",{id:"sytuacja-polityczna-spoleczna-gospodarcza-ziem-polskich"},"Sytuacja polityczna, spo\u0142eczna, gospodarcza ziem polskich",-1),u=i("h4",{id:"zabor-pruski"},"Zab\xF3r Pruski",-1),m=i("p",null,"To terytorium by\u0142o autonomi\u0105. Mo\u017Cna by\u0142o u\u017Cywa\u0107 polskiego, Polacy mogli pe\u0142ni\u0107 funkcje urz\u0119dnicze. By\u0142o r\xF3wnouprawnienie obywateli bez wzgl\u0119du na przynale\u017Cno\u015B\u0107 stanow\u0105 i wyznanie. Gospodarka opiera\u0142a si\u0119 na rolnictwie i przemy\u015Ble przetw\xF3rczym, kt\xF3re pr\u0119\u017Cnie si\u0119 rozwija\u0142y. Zacz\u0105\u0142 si\u0119 kapitalizm z powodu reformy regulacyjnej z 1816, zapocz\u0105tkowa\u0142a ona proces uw\u0142aszczania ch\u0142op\xF3w.",-1),j=i("h4",{id:"galicja"},"Galicja",-1),g=i("p",null,"Wolne Miasto Krak\xF3w nie wchodzi\u0142o w sk\u0142ad Austrii. Mimo i\u017C nie mia\u0142o te\u017C prawa do prowadzenia polityki zagranicznej (reprezentowa\u0142y go 3 pa\u0144stwa opieku\u0144cze [czytaj wy\u017Cej]), to posiada\u0142o w\u0142asny sejm, senat i konstytucj\u0119. Polski by\u0142 j\u0119zykiem urz\u0119dowym. Z powodu wielu wolno\u015Bci, Krak\xF3w sta\u0142 si\u0119 o\u015Brodkiem polskiej kultury, nauki i szkolnictwa. Ekonomicznie to s\u0142abo, brakowa\u0142o \u015Brodk\xF3w na modernizacje. Szlachta utraci\u0142a wiele przywileji, mimo to pa\u0144szczyzn\u0119 zniesiono dopiero w 1848. Istnia\u0142ytam niedu\u017Ce zak\u0142ady przetw\xF3rcze i kopalnie soli w Wieliczce i Bochni.",-1),h=i("h2",{id:"_2-krolestwo-polskie"},"2. Kr\xF3lestwo polskie",-1),_=i("h3",{id:"sytuacja-polityczna-gospodarcza-spoleczna-krolestwa-polskiego"},"Sytuacja polityczna, gospodarcza, spo\u0142eczna Kr\xF3lestwa Polskiego",-1),b=i("p",null,"Znajdowa\u0142o si\u0119 w strefie wp\u0142yw\xF3w Rosji. By\u0142o monarchi\u0105 konstytucyjn\u0105 i car Rosji by\u0142 r\xF3wnie\u017C carem Polski. Gospodarka zacze\u0142a si\u0119 poprawia\u0107 dzi\u0119ki staraniom Franciszka Ksawerego Druckiego-Lubeckiego (zawarcie uk\u0142adu celnego z Prusami, otwarcie rynku rosyjskiego na polskie towary). w 1828 powsta\u0142 Bank Polski. Podstaw\u0105 samej gospodarki by\u0142o rolnictwo, rozwija\u0142o si\u0119 ono wolno, ale stabilnie, najlepiej by\u0142o w powiatach zachodnich. Na wsiach dalej dominowa\u0142a gospodarka folwarczno-pa\u0144szczy\u017Aniana, jednak po otrzymaniu wolno\u015Bci osobistej, wie\u015Bniaki zacze\u0142y cisn\u0105\u0107 do miast, bo tam powstawa\u0142 przemys\u0142. Industrializacja powi\u0119ksza\u0142a wiele miast, m. in. Warszaw\u0119 i \u0141\xF3d\u017A.",-1),P=i("h3",{id:"dzialalnosc-opozycji"},"Dzia\u0142alno\u015B\u0107 Opozycji",-1),W=i("p",null,"Na pocz\u0105tku nikomu nie wadzi\u0142 car. Potem jak w 1820 Aleksander I przyby\u0142 do Warszawy i chcia\u0142 zmieni\u0107 konstytucj\u0119, to si\u0119 zmadowali libera\u0142owie. Pos\u0142owie z wojew\xF3dztwa kaliskiego protestowali w sejmie przeciwko cenzurze (Blokowali zmiany w prawie karnym itd). Zostali jednak pozbawieni dzia\u0142alno\u015Bci, bo car przez 5 lat nie zwo\u0142ywa\u0142 sejmu. Parlament zebra\u0142 si\u0119 dopiero w 1825 potajemnie, bez wiedzy libera\u0142\xF3w.",-1),v=i("h3",{id:"spiski"},"Spiski",-1),K=i("p",null,"W 1821 w\u0142adze zakaza\u0142y tworzy\u0107 jakiekolwiek organizacje. W tym czasie powsta\u0142o wiele sekretnych stowarzysze\u0144, np. Zwi\u0105zek Wolnych Polak\xF3w, Towarzystwo Patriotyczne; to ostatnie powsta\u0142o w 1819 za spraw\u0105 Waleriana \u0141ukasi\u0144skiego pod nazw\u0105 Wolnomularstwo Narodowe (inaczej oddzia\u0142 masonerii - mi\u0119dzynarodowego ruchu szerz\u0105cego idee wolno\u015Bciowe i o\u015Bwieceniowe), planowali oni odbudowa\u0107 niezale\u017Cne pa\u0144stwo w przedrozbiorowych granicach. W 1822 kilku typ\xF3w z\u0142apano, ale organizacja dalej dzia\u0142a\u0142a. Potem ruch rozszerzy\u0142 si\u0119 do Wielkopolski i ziem zachodnich. Rozwi\u0105zali si\u0119 jednak w 1826r. po nieudanym powstaniu dekabryst\xF3w w Rosji.",-1),N=i("h2",{id:"_3-powstanie-listopadowe"},"3. Powstanie Listopadowe",-1),x=i("h3",{id:"noc-listopadowa"},"Noc listopadowa",-1),I=i("p",null,"29 listopada 1830 grupa spiskowc\xF3w zaatakowa\u0142a siedzib\u0119 ksi\u0119cia Konstantego (Belweder). Najprawdopodobniej celem by\u0142o ujmanie go, ale uciek\u0142. Dosz\u0142o do bratobujczych star\u0107 mi\u0119dzy zwolennikami, a przeciwnikami powstania. Lud Warszawy zdoby\u0142 Arsena\u0142 i ruszy\u0142 do walki z oddzia\u0142ami rosyjskimi w stolicy. 30 listopada ju\u017C wyzwolono Warszaw\u0119, a Rosjanie musieli si\u0119 z niej wycofa\u0107.",-1),f=i("h3",{id:"najwazniejsze-bitwy"},"Najwa\u017Cniejsze bitwy:",-1),D=i("div",{class:"table-container"},[i("table",null,[i("thead",null,[i("tr",null,[i("th",{style:{"text-align":"center"}},"Data"),i("th",{style:{"text-align":"center"}},"Miejsce"),i("th",null,"Wynik"),i("th",{style:{"text-align":"center"}},"Dow\xF3dca")])]),i("tbody",null,[i("tr",null,[i("td",{style:{"text-align":"center"}},"14 II 1831"),i("td",{style:{"text-align":"center"}},"Groch\xF3w"),i("td",null,"najwi\u0119ksza, nierozstrzygni\u0119ta bitwa, straty uniemo\u017Cliwiaj\u0105ce Rosjanom atak na Warszaw\u0119"),i("td",{style:{"text-align":"center"}},"gen. J\xF3zef Ch\u0142opicki")]),i("tr",null,[i("td",{style:{"text-align":"center"}},"14 II 1831"),i("td",{style:{"text-align":"center"}},"Ostro\u0142\u0119ka"),i("td",null,"przegrana Polak\xF3w, spadek morali"),i("td",{style:{"text-align":"center"}},"gen. Jan Skrzynecki")])])])],-1),S=i("h3",{id:"postacie"},"Postacie:",-1),R=i("div",{class:"table-container"},[i("table",null,[i("thead",null,[i("tr",null,[i("th",null,"Pic"),i("th",{style:{"text-align":"center"}},"Imi\u0119 i Nazwisko"),i("th",null,"Z czego znan(y/a)")])]),i("tbody",null,[i("tr",null,[i("td",null,[i("img",{src:n,alt:"alt text"})]),i("td",{style:{"text-align":"center"}},"Emilia Plater"),i("td",null,"By\u0142a dow\xF3dczyni\u0105 oddzia\u0142\xF3w partyzanckich na Litwie. Nie chcia\u0142a z\u0142o\u017Cy\u0107 broni w Prusach, pojecha\u0142a do Warszawy i si\u0119 w drodz\u0119 rozchorowa\u0142a na w skutego czego zmar\u0142a.")]),i("tr",null,[i("td",null,[i("img",{src:l,alt:"alt text"})]),i("td",{style:{"text-align":"center"}},"Konstanty Ordon"),i("td",null,"Dow\xF3dca wyst\u0119puj\u0105cy w Reducie Ordona Adama Mickiewicza. Wysadzi\u0142 arsena\u0142 w wyniku czego zgin\u0119\u0142o wielu rosyjskich genera\u0142\xF3w. On sam jednak prze\u017Cy\u0142 powstanie i pope\u0142ni\u0142 samob\xF3jstwo w wieku 77 lat we Florencji.")])])])],-1),B=i("h2",{id:"_4-wielka-emigracja"},"4. Wielka emigracja",-1),G=i("h3",{id:"ugrupowania-na-emigracji"},"Ugrupowania na emigracji",-1),L=i("p",null,"Joachim Lelewel stworzy\u0142 w grudniu 1831 Komitet Narodowy Polski prezentuj\u0105cy pogl\u0105dy republika\u0144skie i krytykuj\u0105cy idee powsta\u0144. Francja go wydali\u0142a w 1833 i dalej kontynuowa\u0142 swoj\u0105 dzia\u0142alno\u015B\u0107 w Brukselii.",-1),M=i("p",null,"Hotel Lambert zosta\u0142 stworzony w 1831 przez Adama Jerzego Czartoryskiego. Celem ich by\u0142o nag\u0142a\u015Bnianie sprawy polskiej na arenie mi\u0119dzynarodowej. Wed\u0142ug nich Polska powinna by\u0107 monarchi\u0105 konstytucyjn\u0105.",-1),Z=i("p",null,[a("Towarzystwo Demokratyczne Polskie to najwi\u0119ksza organizacja emigracyjna, zyskali popularno\u015B\u0107 po og\u0142oszeniu "),i("em",null,"Wielkiego manifestu"),a(". Planowali zrobi\u0107 powstanie, gdzie g\u0142\xF3wn\u0105 rol\u0119 odgrywaliby ch\u0142opi. Polska mia\u0142aby sta\u0107 si\u0119 republik\u0105 i og\xF3lnie mieli takie liberalne pogl\u0105dy.")],-1),J=i("p",null,"M\u0142oda Polska powsta\u0142a w 1834, by\u0142a frakcj\u0105 M\u0142odej Europy Giuseppa Mazziniego. Joachim Lelewel potem do niej sam do\u0142\u0105czy\u0142, chcieli oni wznieci\u0107 powstanie.",-1),A=i("p",null,"Gromada Ludu Polskiego to radyka\u0142owie z ni\u017Cszych warstw spo\u0142ecznych. Komunistami takimi byli i obwiniali szlacht\u0119 za utrat\u0119 niepodleg\u0142o\u015Bci i niesprawiedliwy system spo\u0142eczny.",-1),E=i("h3",{id:"organizacje-spiskowe-w-kraju"},"Organizacje spiskowe w kraju",-1),O=i("p",null,"Stowarzyszenie Ludu Polskiego powsta\u0142o z inicjatywy Szymona Konarskiego w 1835. Prowadzili oni dzia\u0142alno\u015B\u0107 patriotyczn\u0105 (mo\u017Ce by\u0107 co\u015B o Karolu Levittoux, co si\u0119 nie sprzeda\u0142 na policji, spali\u0142 cele i umar\u0142 w p\u0142omieniach).",-1),T=i("p",null,"W Wielkopolsce powsta\u0142a Centralizacja Pozna\u0144ska z Karolem Libelt\u0105 na czele. Innym zwi\u0105zkiem w Poznaniu by\u0142 Zwi\u0105zek Plebejuszy za\u0142o\u017Cony przez Walenty Stefa\u0144ski.",-1),U=i("p",null,"W Warszawie powsta\u0142 Zwi\u0105zek Narodu Polskiego, kt\xF3ry prowadzi\u0142 dzia\u0142alno\u015B\u0107 konspiracyjn\u0105. G\u0142\xF3wnymi jego dzia\u0142aczami byli Henryk Kamie\u0144ski i Edward Dembowski.",-1),X=i("h2",{id:"_5-wiosna-ludow"},"5. Wiosna lud\xF3w",-1),C=i("h3",{id:"rabacja-galicyjska"},"Rabacja galicyjska",-1),F=i("p",null,"Mia\u0142o by\u0107 powstanie w 1846, ale tak do ko\u0144ca nie wybuch\u0142o i by\u0142y tylko pojedy\u0144cze wyst\u0105pienia w Galicji (i Kr\xF3lestwie Kongresowym). Do walk przeciwko powsta\u0144com do\u0142\u0105czyli ch\u0142opi, kt\xF3rym wm\xF3wiono, \u017Ce szlachta zorganizowa\u0142a powstanie, aby chroni\u0107 swoje przywileje przed cesarzem. Wyst\u0105pienia ch\u0142opskie nazwano rabacj\u0105 (lub rzezi\u0105 galicyjsk\u0105). Na czele tego przedsi\u0119wzi\u0119cia sta\u0142 Jakub Szela. Mordowali szlacht\u0119, palili dwory itp. Gdy zagro\u017Cenie powstaniem mine\u0142o wojsko austryjackie rozgoni\u0142o wsiur\xF3w.",-1),$=i("h3",{id:"powstanie-krakowskie"},"Powstanie krakowskie",-1),H=i("p",null,"21 lutego 1846 wybuch\u0142o powstanie w Krakowie. Wojska austriackie by\u0142y zmuszone do opuszczenia miasta. Powsta\u0142 w\xF3wczas Rz\u0105d Narodowy Rzeczpospolitej Polskiej, na kt\xF3rego czele stan\u0105\u0142 Jan Tyssowski, a sekretarzem zosta\u0142 Edward Dembowski. Rz\u0105d wyda\u0142 wkr\xF3tce Manifest do narodu polskiego. By\u0142y tam r\xF3\u017Cne obietnice o zniesieniu pa\u0144szczyzny i pomocy spo\u0142ecznej dla mieszczan. Jednak (mi\u0119dzy innymi) z powodu rabacji powstanie szybko upad\u0142o. Przyczyni\u0142o si\u0119 do upadku Wolnego Miasta Krakowa, kt\xF3re 16 listopada 1846 wcielono do Austrii.",-1),V=i("h3",{id:"wiosna-ludow"},"Wiosna lud\xF3w",-1),q=i("h4",{id:"w-wielkopolsce"},"w Wielkopolsce",-1),Q=i("p",null,"Z powodu rewolucji w Berlinie nasta\u0142 wzrosty patriotycznych nastroj\xF3w w Wielkopolsce. W marcu 1848 na ulice wyszli demonstranci. Powsta\u0142 komitet narodowy i oddzia\u0142y zbrojne. Do Berlina wybra\u0142a si\u0119 delegacja z arcybiskupem Leonem Przy\u0142uskim, mimo i\u017C nie dostali ca\u0142kowitej autonomii, to przywr\xF3cono polsk\u0105 administracj\u0119 i utworzono gwardi\u0119 narodow\u0105. W kwietniu wielkopolski sejm podj\u0105\u0142 decyzj\u0119 o uzale\u017Cnieniu siu\u0119 od Prus. 10 kwietnia naczelnym wodzem og\u0142oszono Ludwika Mierod\u0142awskiego, a dzie\u0144 p\xF3\u017Aniej podpisano ugod\u0119 w Jaros\u0142awcu, ale Prusacy zacz\u0119li j\u0105 \u0142ama\u0107, pacyfikowano wsie itd. W ko\u0144cu, 29 kwietnia, zaatakowali polski ob\xF3z w Ksi\u0105\u017Cu i Wielkopolanie si\u0119 zmadowali. Wa\u017Cne bitwy wygrane przez Polak\xF3w: Mi\u0142os\u0142aw i Soko\u0142\xF3w. To wsyzstko si\u0119 zako\u0144czy\u0142o 9 maja. Polacy przegrali wojn\u0119, ale uzyskali eprezentacj\u0119 w pruskim sejmie w Berlinie.",-1),Y=i("h4",{id:"w-galicji"},"w Galicji",-1),ii=i("p",null,"Rewolucja z Wiednia zach\u0119ci\u0142a Galicjan do ataku na os\u0142abionego zaborc\u0119. W marcu i kwietniu 1848 powsta\u0142y polskie organizacje, kt\xF3re za cel obra\u0142y sobi\u0119 reprezentowanie Polak\xF3w w relacjach z austriackimi w\u0142adzami. Do Wiednia uda\u0142a si\u0119 delegacja i dostali obietnic\u0119 polonizacji szk\xF3\u0142. Polscy dzia\u0142acze nawo\u0142ywali do zwolnienia ch\u0142op\xF3w z pa\u0144szczyznianych obowiwi\u0105zk\xF3w przez szlacht\u0119, co nie by\u0142o na r\u0119k\u0119 Austrii, kt\xF3ra mia\u0142a profity ze sporu szlachta-ch\u0142opi. 22 kwietnia 1848 Gubernator Galicji Franz von Stadion zni\xF3s\u0142 pa\u0144szczyzn\u0119 i uw\u0142aszczy\u0142 ch\u0142op\xF3w. Utrzymano serwituty - prawo ch\u0142op\xF3w do koszystania z pa\u0144skich \u0142\u0105k i las\xF3w. Pod koniec kwietnia Gwardia Narodowa wojowa\u0142a przez chwil\u0119 przeciw wojsku austryjackiemu w Krakowie, ale to niewa\u017Cne, bo szybko przegrali. Jesieni\u0105 1848 zacz\u0119to pacyfikowa\u0107 Lw\xF3w, polskie organizacje przesta\u0142y istnie\u0107. Ale by\u0142y te\u017C plusy, np. Polska reprezentacja znalaz\u0142a si\u0119 w sejmie wiede\u0144skim, a gubernatorem Galicji zosta\u0142 Polak - Wac\u0142aw Zaleski. A i Ukrai\u0144cy zaczeli si\u0119 nie lubi\u0107 z Polakami, bo ich obaj mieli te no... niepodleg\u0142o\u015Bciowe ch\u0119ci czy co\u015B.",-1),ai=[c,z,r,d,p,k,y,u,m,j,g,h,_,b,P,W,v,K,N,x,I,f,D,S,R,B,G,L,M,Z,J,A,E,O,T,U,X,C,F,$,H,V,q,Q,Y,ii];function oi(ei,si,ti,ni,li,wi){return s(),e("div",null,ai)}var ri=o(w,[["render",oi]]);export{zi as __pageData,ri as default};
