import{_ as i,o as e,c as a,f as t}from"./app.74e3ecb9.js";var o="/3-dzial/cavour.jpg",n="/3-dzial/garbibaldi.jpg",l="/3-dzial/emanuel.jpg",r="/3-dzial/lincoln.jpg",c="/3-dzial/davis.jpg",d="/3-dzial/lee.jpg",z="/3-dzial/sherman.jpg",s="/3-dzial/grant.jpg",w="/3-dzial/krolowa.jpg",y="/3-dzial/mandolina.jpg";const W='{"title":"Dzia\u0142 III - Europa i \u015Bwiat w drugiej po\u0142owie XIX wieku","description":"Notatka dotycz\u0105ca III rozdzia\u0142u podr\u0119cznika \\"Pozna\u0107 przesz\u0142o\u015B\u0107 3\\" wydawnictwa Nowej Ery.","frontmatter":{"title":"Dzia\u0142 III - Europa i \u015Bwiat w drugiej po\u0142owie XIX wieku","description":"Notatka dotycz\u0105ca III rozdzia\u0142u podr\u0119cznika \\"Pozna\u0107 przesz\u0142o\u015B\u0107 3\\" wydawnictwa Nowej Ery."},"headers":[{"level":2,"title":"1. Od Wojny Krymskiej do zjednoczenia W\u0142och","slug":"_1-od-wojny-krymskiej-do-zjednoczenia-wloch"},{"level":3,"title":"Terminy:","slug":"terminy"},{"level":3,"title":"Postacie:","slug":"postacie"},{"level":3,"title":"Etapy zjednoczenia W\u0142och","slug":"etapy-zjednoczenia-wloch"},{"level":2,"title":"2. Zjednoczenie Niemiec","slug":"_2-zjednoczenie-niemiec"},{"level":3,"title":"Koncepcje zjednoczenia pa\u0144stwa:","slug":"koncepcje-zjednoczenia-panstwa"},{"level":3,"title":"Poj\u0119cia","slug":"pojecia"},{"level":3,"title":"Etapy zjednoczenia:","slug":"etapy-zjednoczenia"},{"level":2,"title":"3. Stany Zjednoczone XIX wieku","slug":"_3-stany-zjednoczone-xix-wieku"},{"level":3,"title":"Poj\u0119cia","slug":"pojecia-2"},{"level":3,"title":"Wojna secesyjna","slug":"wojna-secesyjna"},{"level":2,"title":"4. Ekspansja kolonialna","slug":"_4-ekspansja-kolonialna"},{"level":3,"title":"Poj\u0119cia","slug":"pojecia-3"},{"level":3,"title":"Kolonie brytyjskie","slug":"kolonie-brytyjskie"},{"level":2,"title":"5. Post\u0119p naukowy i techniczny","slug":"_5-postep-naukowy-i-techniczny"},{"level":3,"title":"Wynalazki","slug":"wynalazki"},{"level":3,"title":"Koncepcja Darwina","slug":"koncepcja-darwina"},{"level":2,"title":"6. Nowe Ideologie i ruchy spo\u0142eczne","slug":"_6-nowe-ideologie-i-ruchy-spoleczne"},{"level":3,"title":"Poj\u0119cia","slug":"pojecia-4"},{"level":3,"title":"Ruch na rzecz r\xF3wnouprawnie\u0144 kobiet","slug":"ruch-na-rzecz-rownouprawnien-kobiet"},{"level":2,"title":"7. Epoka pozytywizmu","slug":"_7-epoka-pozytywizmu"},{"level":3,"title":"Scientyzm i pozytywizm","slug":"scientyzm-i-pozytywizm"},{"level":3,"title":"Literatura pozytywistyczna","slug":"literatura-pozytywistyczna"},{"level":3,"title":"Sztuka i architektura","slug":"sztuka-i-architektura"},{"level":3,"title":"Nowe nurty","slug":"nowe-nurty"},{"level":3,"title":"Kultura masowa","slug":"kultura-masowa"},{"level":3,"title":"Aktywny Wypoczynek","slug":"aktywny-wypoczynek"}],"relativePath":"pages/Polski/Szkola/Historia/3-dzial.md","lastUpdated":1709057980554}',p={},k=t('<h1 id="dzial-iii-europa-i-swiat-w-drugiej-polowie-xix-wieku">Dzia\u0142 III - Europa i \u015Bwiat w drugiej po\u0142owie XIX wieku</h1><h2 id="_1-od-wojny-krymskiej-do-zjednoczenia-wloch">1. Od Wojny Krymskiej do zjednoczenia W\u0142och</h2><h3 id="terminy">Terminy:</h3><ul><li>Odwil\u017C posewastopolska - liberalizacja polityki wewn\u0119trznej Rosji przez Aleksandra II po przegranej wojnie krymskiej z Imperium Osma\u0144skim.</li><li>Panslawizm - wszyscy S\u0142owianie powinni by\u0107 pod protektoratem cara.</li><li>Risorgimento - ruch, kt\xF3rego celem by\u0142o budzenie w\u0142oskiej \u015Bwiadomo\u015Bci narodowej i d\u0105\u017Cenie do zjednoczenia W\u0142och.</li><li>tysi\u0105ca czerwonych koszul - grupa bojownik\xF3w zorganizowana przez Garibaldiego, kt\xF3ra walczy\u0142a od Sycyli po Neapol.</li></ul><h3 id="postacie">Postacie:</h3><div class="table-container"><table><thead><tr><th>Pic</th><th style="text-align:center;">Imi\u0119 i Nazwisko</th><th>Z czego znan(y/a)</th></tr></thead><tbody><tr><td><img src="'+o+'" alt="alt text"></td><td style="text-align:center;">Camillo Cavour</td><td>W\u0142oski polityk, nawo\u0142ywa\u0142 do zjednoczenia W\u0142och. Zosta\u0142 premierem W\u0142och.</td></tr><tr><td><img src="'+n+'" alt="alt text"></td><td style="text-align:center;">Giuseppe Garibaldi</td><td>W\u0142oski genera\u0142, walczy\u0142 o wyzwolenie i zjednoczenie W\u0142och. Stworzy\u0142 marsz tysi\u0105ca czerwonych koszul i znacz\u0105co przyczyni\u0142 si\u0119 do obalenia monarchii w Kr\xF3lestwie Obojga Sycylii.</td></tr><tr><td><img src="'+l+'" alt="alt text"></td><td style="text-align:center;">Wiktor Emanuel II</td><td>Pierwszy kr\xF3l Kr\xF3lestwa W\u0142och. Pod jego patronatem mia\u0142o okaza\u0107 si\u0119 zjednoczenie p\xF3\u0142nocnych W\u0142och.</td></tr></tbody></table></div><h3 id="etapy-zjednoczenia-wloch">Etapy zjednoczenia W\u0142och</h3><ol><li>Wojna Kr\xF3lestwa Sardynii z Austri\u0105, 1859.</li><li>Powstania i plebiscyty w pa\u0144stwach p\xF3\u0142nocnow\u0142oskich.</li><li>Wyprawa tysi\u0105ca Czerwonych Koszul, 1860.</li><li>Zjednoczenie W\u0142och, 1861.</li><li>Przy\u0142\u0105czenie Wenecji, 1866.</li><li>Przy\u0142\u0105czenie Rzymu, 1870.</li></ol><h2 id="_2-zjednoczenie-niemiec">2. Zjednoczenie Niemiec</h2><h3 id="koncepcje-zjednoczenia-panstwa">Koncepcje zjednoczenia pa\u0144stwa:</h3><ul><li>Ma\u0142e Niemcy - mia\u0142o si\u0119 sk\u0142ada\u0107 jedynie z kraj\xF3w Zwi\u0105zeku Niemieckiego.</li><li>Wielkie Niemcy - Ma\u0142e Niemcy + Cesarstwo Austriackie + panowanie Habsburg\xF3w.</li></ul><h3 id="pojecia">Poj\u0119cia</h3><ul><li>Niemiecki Zwi\u0105zek Celny - powsta\u0142 w 1834 z inicjatywy Prus, wesz\u0142o do niego 18 pa\u0144stw. Zlikwidowa\u0142o to bariery celne, co przyczyni\u0142o si\u0119 do rozwoju handlu i utworzenia og\xF3lnoniemieckiego rynku wewn\u0119trznego.</li><li>Krwi\u0105 i \u017Belazem - u\u017Cycie si\u0142y (w tym kontek\u015Bcie do zjednoczenia Niemiec).</li></ul><h3 id="etapy-zjednoczenia">Etapy zjednoczenia:</h3><ul><li>Wojna Prus i Austrii z Dani\u0105, 1864.</li><li>Wojna Prus z Austri\u0105, 1866.</li><li>Powstanie Zwi\u0105zku P\xF3\u0142nocnoniemieckiego, 1867.</li><li>Wojna prusko-francuska, 1870.</li><li>Og\u0142oszenie zjednoczenia pa\u0144stwa, 1871.</li></ul><h2 id="_3-stany-zjednoczone-xix-wieku">3. Stany Zjednoczone XIX wieku</h2><h3 id="pojecia-2">Poj\u0119cia</h3><ul><li>Doktryna Monroego - nieingerowanie USA w sprawy Europejskie i na odwr\xF3t (jest to forma izolacjonizmu).</li><li>izolacjonizm - polityka jakiego\u015B pa\u0144stwa polegaj\u0105ca na separowaniu si\u0119 od innych pa\u0144stw.</li><li>abolicjonizm - ruch zwalczaj\u0105cy i d\u0105\u017C\u0105cy do zniesienia niewolnictwa.</li></ul><p>Na po\u0142udniu du\u017Co czerpano z niewolnictwa, dlatego po\u0142udniowe stany nie chcia\u0142y go znie\u015B\u0107. Stany p\xF3\u0142nocne natomiast znios\u0142y niewolnictwo.</p><h3 id="wojna-secesyjna">Wojna secesyjna</h3><p>Bitwa pod Gettysburgiem - toczy\u0142a si\u0119 od 1 do 3 lipca 1863, konfederaci pod dow\xF3dztwen genera\u0142a Lee ponie\u015Bli kl\u0119sk\u0119. Zgine\u0142o 50k \u017Co\u0142nierzy.</p><h4 id="postacie-2">Postacie</h4><div class="table-container"><table><thead><tr><th>Pic</th><th style="text-align:center;">Imi\u0119 i Nazwisko</th><th>Z czego znan(y/a)</th></tr></thead><tbody><tr><td><img src="'+r+'" alt="alt text"></td><td style="text-align:center;">Abraham Lincoln</td><td>Prezydent USA. Przez niego by\u0142a wojna secesyjna, bo chcia\u0142 zdelegalizowa\u0107 niewolnictwo. Jak wygra\u0142a Unia to go reelektowano.</td></tr><tr><td><img src="'+c+'" alt="alt text"></td><td style="text-align:center;">Jefferson Davis</td><td>Prezydent Skonfederowanych Stan\xF3w Ameryki.</td></tr><tr><td><img src="'+d+'" alt="alt text"></td><td style="text-align:center;">Robert Lee</td><td>G\u0142\xF3wnodowodz\u0105cy wojsk Konfederacji podczas wojny secesyjnej.</td></tr><tr><td><img src="'+z+'" alt="alt text"></td><td style="text-align:center;">William Sherman</td><td>Genera\u0142 wojsk Unii.</td></tr><tr><td><img src="'+s+'" alt="alt text"></td><td style="text-align:center;">Ulisses Grant</td><td>Genera\u0142 wojsk Unii.</td></tr></tbody></table></div><p>W 1863 Lincoln og\u0142osi\u0142 dekret o zniesieniu niewolnictwa w stanach po\u0142udniowych, kt\xF3rego stany po\u0142udniowe nie uzna\u0142y. Dzi\u0119ki zniesieniu niewolnictwa armia P\xF3\u0142nocy zyska\u0142a zaszczyk \u017Co\u0142nierzy - wyzowlonych niewolnik\xF3w.</p><h2 id="_4-ekspansja-kolonialna">4. Ekspansja kolonialna</h2><h3 id="pojecia-3">Poj\u0119cia</h3><ul><li>kolonializm - system uzale\u017Cniaj\u0105cy kraj\xF3w o ni\u017Cszym poziomie rozwoju przez pot\u0119\u017Cne pa\u0144stwa.</li><li>imperializm - polityka polegaj\u0105ca na podbojach kolonialnych lub d\u0105\u017Ceniu do podporz\u0105dkowania sobie innych kraj\xF3w.</li><li>dominium - niezale\u017Cne pa\u0144stwo wchodz\u0105ce w sk\u0142ad brytyjskiej Wsp\xF3lnoty Narod\xF3w oraz uznaj\u0105ce monarch\u0119 brytyjskiego za g\u0142ow\u0119 pa\u0144stwa.</li><li>p\xF3\u0142kolonia - gospodarczo uzale\u017Cnione, ale formalnie niepodleg\u0142e pa\u0144stwa.</li></ul><h3 id="kolonie-brytyjskie">Kolonie brytyjskie</h3><ul><li>Kanada,</li><li>Po\u0142udniowa Afyka,</li><li>Egipt,</li><li>Indie,</li><li>Australia,</li><li>Nowa Zelandia,</li><li>Nigeria.</li></ul><p>Imperium, nad kt\xF3rym nigdy nie zachodzi s\u0142o\u0144ce - imperium tak rozleg\u0142e, \u017Ce w dowolnym momencie na jego powierzchni jest widno. Np. Imperium hiszpa\u0144skie, brytyjskie.</p><div class="table-container"><table><thead><tr><th>Nazwa</th><th>Czas</th><th>Miejsce</th><th>Przyczyny</th><th>Skutki</th></tr></thead><tbody><tr><td>Powstanie Dipo Negoro</td><td>1825-1830</td><td>Holenderskie Indie Wschodnie</td><td>Trudna sytuacja autochton\xF3w, konflikt religijny mi\u0119dzy Holendrami a ludno\u015Bci\u0105 muzu\u0142ma\u0144sk\u0105</td><td>Zwyci\u0119stwo Holendr\xF3w, narzucanie dodatkowych podatk\xF3w</td></tr><tr><td>I wojna opiumowa</td><td>1839-1841</td><td>Chiny</td><td>Ingerencja Wielkiej Brytnii w wewn\u0119trzne sprawy Chin, sprzeciw w\u0142adz chi\u0144skich wobec sprowadzania opium</td><td>Kl\u0119ska Chin, otwarcie port\xF3w dla obcych statk\xF3w, strefy wp\u0142yw\xF3w Europejczyk\xF3w, oddanie Hongkongu Brytyjczykom.</td></tr><tr><td>Powstanie tajping\xF3w</td><td>1851-1864</td><td>Chiny</td><td>Op\xF3r wobec w\u0142adzy uleg\u0142ej kolonizatorom i niesprawiedliwo\u015Bci spo\u0142rcznej.</td><td>Os\u0142abienie Chin, wielkie straty w ludziach, rozszerzenie wp\u0142yw\xF3w mocarstw europejskich.</td></tr><tr><td>Powstanie sipaj\xF3w</td><td>1857-1859</td><td>Indie</td><td>walka Brytyjczyk\xF3w z lokalnumi tradycjami i podzia\u0142em kastowym.</td><td>Kl\u0119ska powsta\u0144c\xF3w, centralizacja w\u0142adzy, pozostawienie podzia\u0142u kastowego i mniejsza ingerencja w tradycje.</td></tr><tr><td>Ruch czarnych flag</td><td>Od pocz\u0105tku lat 80. XIX w.</td><td>Wietnam</td><td>Kolonia francuska, wspieranie przez Francuz\xF3w dot\u0105d dyskryminowanych wyznawc\xF3w katolicyzm</td><td>St\u0142umienie ruchu i zbrojna pacyfikacja Wietnamu</td></tr><tr><td>Powstanie Mahdiego</td><td>1881-1899</td><td>Sudan</td><td>Ekspancja Brytyjczk\xF3w w Egipcie i Sudanie, sprzeciw wobec w\u0142adzy Brytyjczyk\xF3w i Egipcjan, utworzenie pa\u0144stwa islamskiego.</td><td>Kl\u0119ska mahdyst\xF3w i upadek ich pa\u0144stwa.</td></tr><tr><td>Powstanie bokser\xF3w</td><td>1899-1901</td><td>Chiny</td><td>Polityka mocarstw kolonialnych, ingerencja w sprawy Chin, modernizacja pa\u0144stw.</td><td>Chiny pod ca\u0142kowit\u0105 kontrol\u0105 mocarstw, wzmo\u017Cona chrystianizacja Chin.</td></tr></tbody></table></div><h2 id="_5-postep-naukowy-i-techniczny">5. Post\u0119p naukowy i techniczny</h2><h3 id="wynalazki">Wynalazki</h3><div class="table-container"><table><thead><tr><th>Posta\u0107(ie)</th><th>Wynalazek</th></tr></thead><tbody><tr><td>Charles Darwin</td><td>teoria ewolucji</td></tr><tr><td>Dimitrj Mendelejew</td><td>uk\u0142ad okresowy pierwiask\xF3w</td></tr><tr><td>Albert Einstein</td><td>teoria wzgl\u0119dno\u015Bci</td></tr><tr><td>Alexander Graham Bell</td><td>telefon</td></tr><tr><td>Thomas Edison</td><td>\u017Car\xF3wka</td></tr><tr><td>Alfred Nobel</td><td>dynamit</td></tr><tr><td>Carl Benz i Gottlieb Daimler</td><td>samoch\xF3d z silnikiem benzynowym</td></tr><tr><td>Henry Ford</td><td>pierwsza ta\u015Bma produkcyjna</td></tr></tbody></table></div><h3 id="koncepcja-darwina">Koncepcja Darwina</h3><p>Teoria ewolucji stworzona przez Darwina, wyja\u015Bniaj\u0105ca powstawanie gatunk\xF3w doborem naturalnym i walk\u0105 o byt.</p><h2 id="_6-nowe-ideologie-i-ruchy-spoleczne">6. Nowe Ideologie i ruchy spo\u0142eczne</h2><h3 id="pojecia-4">Poj\u0119cia</h3><ul><li>Socjaldemokracja - og\xF3lnie to ma by\u0107 rewolucja socjalistyczna, a \u015Brodki produkcji maj\u0105 by\u0107 upa\u0144stwowione. Ale trzeba w\u0142\u0105czy\u0107 si\u0119 w dzia\u0142alno\u015B\u0107 na demokratycznej scenie politycznej.</li><li>Rewizjonizm (reformizm) - d\u0105\u017Cenie do zmian w prawie wyborczym, \u017Ceby mogli robotnicy zasi\u0105\u015B\u0107 w parlamencie.</li><li>I Mi\u0119dzynarod\xF3wka - mi\u0119dzynarodowa organizacja robotnicza za\u0142o\u017Cona po powstaniu styczniowym (dok\u0142adnie 1864) na jakim\u015B meet&#39;cie z Marksem w Londynie.</li><li>Chadecja - chrze\u015Bcija\u0144ska demokcjacja, mia\u0142a konkurowa\u0107 z wp\u0142ywami socjalistycznymi narastaj\u0105cymi w\u015Br\xF3d chrze\u015Bcijan.</li><li>Nacjonalizm - pa\u0144stwa powinny mie\u0107 charakter narodowy, a to\u017Csamo\u015B\u0107 narodowa jest najwa\u017Cniejszym elementem to\u017Csamo\u015Bci ka\u017Cdego cz\u0142owieka.</li></ul><h3 id="ruch-na-rzecz-rownouprawnien-kobiet">Ruch na rzecz r\xF3wnouprawnie\u0144 kobiet</h3><p>No kobiety zacze\u0142y walczy\u0107 o swoje prawa itd. Te kobiety nazywano emancypantkami i walczy\u0142y o: dost\u0119p do wykszta\u0142cenia, zmianny w prawie rodzinnym, r\xF3wnouprawnienie ekonomiczne i prawa wyborcze. I powoli im si\u0119 udawa\u0142o te postulaty realizowa\u0107. Sufra\u017Cystki walczy\u0142y g\u0142\xF3wnie o prawa wyborcze i najpierw nadano im to prawo w stanie Wyoming w 1869. W Polsce kobiety dosta\u0142y prawa wyborcze w 1918 wraz z odzyskaniem niepodleg\u0142o\u015Bci.</p><h2 id="_7-epoka-pozytywizmu">7. Epoka pozytywizmu</h2><h3 id="scientyzm-i-pozytywizm">Scientyzm i pozytywizm</h3><p>Scjentyzm odrzuca emocjonalne, irracjonalne podej\u015Bcie do nieznanych zjawisk. Scjenty\u015Bci za jedyne narz\u0119dzie poznawania \u015Bwiata uznawali metody naukowe.</p><h3 id="literatura-pozytywistyczna">Literatura pozytywistyczna</h3><p>W literaturze tego okresu pojawi\u0142 si\u0119 realizm i naturalizm. Autorzy powie\u015Bci realistycznych przedstawiali obraz spo\u0142ecze\u0144stwa epoki industrialnej i \u017Cycia ludzi biednych. W ten spos\xF3b kszta\u0142towano w spo\u0142ecze\u0144stwie wra\u017Cliwo\u015B\u0107 na tematy wcze\u015Bniej bagatelizowane.</p><h3 id="sztuka-i-architektura">Sztuka i architektura</h3><p>W po\u0142owie XIX w. zacz\u0105\u0142 kszta\u0142towa\u0107 si\u0119 realizm - przedstawianie \u017Cycia codziennego. Cz\u0119sto ukazywa\u0142y si\u0119 dzie\u0142a, kt\xF3re zawiera\u0142y przekaz moralny, polityczny.</p><p>W drugiej po\u0142owie XIX w. powsta\u0142 impresjonizm - malowanie pejza\u017Cy, scen z \u017Cycia miejskiego za pomoc\u0105 refleks\xF3w \u015Bwiat\u0142a i czystych barw.</p><p>W architekturze nawi\u0105zywano do styl\xF3w minionych epok, nazwano to historyzmem.</p><p>Pod koniec XIX w. historyzm zacz\u0119\u0142a wypiera\u0107 secesja, kt\xF3r\u0105 charakteryzowa\u0142a bogata ornamentyka, motywy ro\u015Blinne, geometryczne i zwierz\u0119ce.</p><h3 id="nowe-nurty">Nowe nurty</h3><p>Opr\xF3cz impresjonizmu i secesji w epoce pozytywizmu powsta\u0142y inne nurty:</p><ul><li>Symbolizm - nawi\u0105zuje w formie do malarstwa realistycznego, skupia si\u0119 jednak na obrazowaniu idei i emocji.</li></ul><p class="center"><img src="'+w+'" alt="alt text"></p><ul><li>Kubizm - opiera si\u0119 na przedstawianiu malowanych obiekt\xF3w jako bry\u0142, zosta\u0142 zainicjowany przez Georges\u2019a Braque\u2019a i Pabla Picassa.</li></ul><p class="center"><img src="'+y+'" alt="alt text"></p><h3 id="kultura-masowa">Kultura masowa</h3><p>Powstaje zjawisko kultury masowej, czyli kierowanej do przeci\u0119tnego odbiorcy. Prasa zaczyna wychodzi\u0107 w coraz wi\u0119kszych nak\u0142adach, ukazuj\u0105 si\u0119 w niej komiksy, wiadomo\u015Bci, powie\u015Bci i reklamy. Dzienniki, ilustrowane magazyny i literatura popularna. Narodzi\u0142o si\u0119 i zacze\u0142o si\u0119 szybko rozwija\u0107 kino.</p><h3 id="aktywny-wypoczynek">Aktywny Wypoczynek</h3><p>Coraz wi\u0119cej os\xF3b zaczyna aktywnie sp\u0119dza\u0107 czas wolny. Upowszechni\u0142y si\u0119 turystyka, kultura fizyczna i sport. Z czasem sport zacz\u0119to bra\u0107 na serio, powstawa\u0142y profesionalne kluby sportowe, a w 1896 wskrzeszono igrzyska. W 1907 powsta\u0142 nowy system wychowania - skauting. Polega\u0142 na uzupe\u0142nieniu kszta\u0142cenia szkolnego poprzez rozw\xF3j sprawno\u015Bciowy w duchu patriotyzmu i poszanowania natury. Rodziny zacz\u0119\u0142y implementowa\u0107 ten system - starsze rodze\u0144stwo jest wzorem dla m\u0142odszych dzieci i si\u0119 nim opiekuje.</p>',61),u=[k];function j(m,h,g,b,_,v){return e(),a("div",null,u)}var I=i(p,[["render",j]]);export{W as __pageData,I as default};
